import React, { useRef } from 'react';
import { useSimulatorStore } from '../store/simulatorStore';

const ControlPanel: React.FC = () => {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const {
    mode,
    isRunning,
    isPaused,
    animationSpeed,
    currentStep,
    totalSteps,
    setMode,
    setAnimationSpeed,
    step,
    run,
    pause,
    reset,
    jumpToStep,
    loadProgram,
    setSourceCode,
  } = useSimulatorStore();

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target?.result as string;
      setSourceCode(content);

      // Parse assembly code (simplified)
      const lines = content.split('\n').filter(line => line.trim());
      const instructions = lines.map((line, index) => ({
        address: index * 4,
        machineCode: '00000000',
        assembly: line.trim(),
        type: 'R' as const,
        fields: { opcode: line.split(' ')[0] }
      }));

      loadProgram(instructions);
    };
    reader.readAsText(file);
  };

  const handleStepInput = (event: React.ChangeEvent<HTMLInputElement>) => {
    const step = parseInt(event.target.value);
    if (!isNaN(step)) {
      jumpToStep(step - 1); // Convert to 0-based index
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6 space-y-6">
      <h2 className="text-xl font-bold text-gray-800">LEGv8 Simulator Control</h2>

      {/* Mode Toggle */}
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">
          Simulation Mode
        </label>
        <div className="flex space-x-4">
          <button
            onClick={() => setMode('simulation')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              mode === 'simulation'
                ? 'bg-cpu-blue text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            🎬 Simulation
          </button>
          <button
            onClick={() => setMode('realtime')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              mode === 'realtime'
                ? 'bg-cpu-green text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            ⚡ Realtime
          </button>
        </div>
      </div>

      {/* File Input */}
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">
          Load Program
        </label>
        <input
          ref={fileInputRef}
          type="file"
          accept=".s,.asm,.legv8"
          onChange={handleFileUpload}
          className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-medium file:bg-cpu-blue file:text-white hover:file:bg-blue-600"
        />
        <p className="text-xs text-gray-500">
          Upload LEGv8 assembly (.s) or executable files
        </p>
      </div>

      {/* Execution Controls */}
      <div className="space-y-4">
        <label className="block text-sm font-medium text-gray-700">
          Execution Control
        </label>

        <div className="flex space-x-2">
          <button
            onClick={step}
            disabled={isRunning && !isPaused}
            className="flex-1 px-4 py-2 bg-cpu-yellow text-white rounded-lg font-medium hover:bg-yellow-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            ⏭️ Step
          </button>

          <button
            onClick={isRunning ? pause : run}
            className={`flex-1 px-4 py-2 rounded-lg font-medium transition-colors ${
              isRunning
                ? 'bg-cpu-red text-white hover:bg-red-600'
                : 'bg-cpu-green text-white hover:bg-green-600'
            }`}
          >
            {isRunning ? '⏸️ Pause' : '▶️ Run'}
          </button>

          <button
            onClick={reset}
            className="flex-1 px-4 py-2 bg-gray-500 text-white rounded-lg font-medium hover:bg-gray-600 transition-colors"
          >
            🔄 Reset
          </button>
        </div>
      </div>

      {/* Step Navigation */}
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">
          Step Navigation
        </label>
        <div className="flex items-center space-x-2">
          <span className="text-sm text-gray-600">Step:</span>
          <input
            type="number"
            min="1"
            max={totalSteps}
            value={currentStep + 1}
            onChange={handleStepInput}
            className="w-20 px-2 py-1 border border-gray-300 rounded text-sm"
          />
          <span className="text-sm text-gray-600">of {totalSteps}</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div
            className="bg-cpu-blue h-2 rounded-full transition-all duration-300"
            style={{ width: `${totalSteps > 0 ? (currentStep / totalSteps) * 100 : 0}%` }}
          />
        </div>
      </div>

      {/* Animation Speed (only in simulation mode) */}
      {mode === 'simulation' && (
        <div className="space-y-2">
          <label className="block text-sm font-medium text-gray-700">
            Animation Speed: {animationSpeed}x
          </label>
          <input
            type="range"
            min="0.1"
            max="3"
            step="0.1"
            value={animationSpeed}
            onChange={(e) => setAnimationSpeed(parseFloat(e.target.value))}
            className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
          />
          <div className="flex justify-between text-xs text-gray-500">
            <span>0.1x</span>
            <span>1x</span>
            <span>3x</span>
          </div>
        </div>
      )}

      {/* Status */}
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">Status</label>
        <div className="text-sm space-y-1">
          <div className="flex justify-between">
            <span>Mode:</span>
            <span className="font-medium capitalize">{mode}</span>
          </div>
          <div className="flex justify-between">
            <span>State:</span>
            <span className={`font-medium ${
              isRunning ? 'text-cpu-green' : 'text-gray-600'
            }`}>
              {isRunning ? (isPaused ? 'Paused' : 'Running') : 'Stopped'}
            </span>
          </div>
          <div className="flex justify-between">
            <span>Instructions:</span>
            <span className="font-medium">{totalSteps}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ControlPanel;
