import React, { useEffect, useRef } from 'react';
import { useSimulatorStore } from '../../store/simulatorStore';
import { animationController } from '../../utils/animations';
import ProgramCounter from './ProgramCounter';
import InstructionMemory from './InstructionMemory';
import RegisterFile from './RegisterFile';
import ALU from './ALU';
import DataMemory from './DataMemory';
import ControlUnit from './ControlUnit';
import DataPaths from './DataPaths';

const CPUDatapath: React.FC = () => {
  const svgRef = useRef<SVGSVGElement>(null);
  const { mode, cpu, animationSpeed } = useSimulatorStore();

  useEffect(() => {
    // Set animation speed when it changes
    animationController.setSpeed(animationSpeed);
  }, [animationSpeed]);

  useEffect(() => {
    // Clear animations when switching modes
    if (mode === 'realtime') {
      animationController.clear();
    }
  }, [mode]);

  return (
    <div className="relative w-full h-full bg-white rounded-lg shadow-lg p-4 overflow-hidden">
      {/* CPU Architecture Diagram */}
      <svg
        ref={svgRef}
        className="absolute inset-0 w-full h-full"
        viewBox="0 0 1200 800"
        preserveAspectRatio="xMidYMid meet"
      >
        {/* Data paths and control signals */}
        <DataPaths />

        {/* Background grid for better visualization */}
        <defs>
          <pattern id="grid" width="20" height="20" patternUnits="userSpaceOnUse">
            <path d="M 20 0 L 0 0 0 20" fill="none" stroke="#f1f5f9" strokeWidth="1"/>
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#grid)" opacity="0.5" />
      </svg>

      {/* CPU Components positioned absolutely */}
      <div className="relative z-10 w-full h-full">
        {/* Program Counter */}
        <div 
          className="absolute"
          style={{ left: '50px', top: '400px', width: '80px', height: '60px' }}
          data-component="pc"
        >
          <ProgramCounter />
        </div>

        {/* Instruction Memory */}
        <div 
          className="absolute"
          style={{ left: '180px', top: '350px', width: '120px', height: '160px' }}
          data-component="instruction-memory"
        >
          <InstructionMemory />
        </div>

        {/* Control Unit */}
        <div 
          className="absolute"
          style={{ left: '400px', top: '200px', width: '200px', height: '300px' }}
          data-component="control-unit"
        >
          <ControlUnit />
        </div>

        {/* Register File */}
        <div 
          className="absolute"
          style={{ left: '350px', top: '550px', width: '150px', height: '200px' }}
          data-component="registers"
        >
          <RegisterFile />
        </div>

        {/* ALU */}
        <div 
          className="absolute"
          style={{ left: '650px', top: '450px', width: '120px', height: '150px' }}
          data-component="alu"
        >
          <ALU />
        </div>

        {/* Data Memory */}
        <div 
          className="absolute"
          style={{ left: '900px', top: '400px', width: '150px', height: '200px' }}
          data-component="data-memory"
        >
          <DataMemory />
        </div>

        {/* Multiplexers */}
        <div 
          className="absolute"
          style={{ left: '550px', top: '600px', width: '40px', height: '60px' }}
          data-component="mux-1"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold">
            MUX
          </div>
        </div>

        <div 
          className="absolute"
          style={{ left: '800px', top: '350px', width: '40px', height: '60px' }}
          data-component="mux-2"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold">
            MUX
          </div>
        </div>

        <div 
          className="absolute"
          style={{ left: '1100px', top: '300px', width: '40px', height: '60px' }}
          data-component="mux-3"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold">
            MUX
          </div>
        </div>

        {/* Adders */}
        <div 
          className="absolute"
          style={{ left: '150px', top: '300px', width: '60px', height: '40px' }}
          data-component="pc-adder"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-cpu-green bg-opacity-20">
            +4
          </div>
        </div>

        <div 
          className="absolute"
          style={{ left: '750px', top: '250px', width: '60px', height: '40px' }}
          data-component="branch-adder"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-cpu-green bg-opacity-20">
            ADD
          </div>
        </div>

        {/* Shift Left 2 */}
        <div 
          className="absolute"
          style={{ left: '650px', top: '300px', width: '60px', height: '30px' }}
          data-component="shift-left"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-cpu-yellow bg-opacity-20">
            &lt;&lt;2
          </div>
        </div>

        {/* Sign Extend */}
        <div 
          className="absolute"
          style={{ left: '300px', top: '650px', width: '80px', height: '40px' }}
          data-component="sign-extend"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-cpu-blue bg-opacity-20">
            Sign Ext
          </div>
        </div>
      </div>

      {/* Current instruction indicator */}
      {cpu.currentInstruction && (
        <div className="absolute top-4 right-4 bg-cpu-blue text-white px-3 py-2 rounded-lg text-sm font-mono">
          <div className="font-bold">Current Instruction:</div>
          <div>{cpu.currentInstruction.assembly}</div>
          <div className="text-xs opacity-75">
            PC: 0x{cpu.pc.toString(16).toUpperCase().padStart(8, '0')}
          </div>
        </div>
      )}

      {/* Animation mode indicator */}
      <div className="absolute top-4 left-4 flex items-center space-x-2">
        <div className={`w-3 h-3 rounded-full ${mode === 'simulation' ? 'bg-cpu-green animate-pulse' : 'bg-cpu-gray'}`} />
        <span className="text-sm font-medium capitalize">{mode} Mode</span>
      </div>
    </div>
  );
};

export default CPUDatapath;
