import React, { useEffect, useRef } from 'react';
import { useSimulatorStore } from '../../store/simulatorStore';
import { animationController } from '../../utils/animations';
import ProgramCounter from './ProgramCounter';
import InstructionMemory from './InstructionMemory';
import RegisterFile from './RegisterFile';
import ALU from './ALU';
import DataMemory from './DataMemory';
import ControlUnit from './ControlUnit';
import DataPaths from './DataPaths';

const CPUDatapath: React.FC = () => {
  const svgRef = useRef<SVGSVGElement>(null);
  const { mode, cpu, animationSpeed } = useSimulatorStore();

  useEffect(() => {
    // Set animation speed when it changes
    animationController.setSpeed(animationSpeed);
  }, [animationSpeed]);

  useEffect(() => {
    // Clear animations when switching modes
    if (mode === 'realtime') {
      animationController.clear();
    }
  }, [mode]);

  return (
    <div className="relative w-full h-full bg-white rounded-lg shadow-lg p-4 overflow-hidden">
      {/* CPU Architecture Diagram */}
      <svg
        ref={svgRef}
        className="absolute inset-0 w-full h-full"
        viewBox="0 0 1400 900"
        preserveAspectRatio="xMidYMid meet"
      >
        {/* Data paths and control signals */}
        <DataPaths />

        {/* Background grid for better visualization */}
        <defs>
          <pattern id="grid" width="20" height="20" patternUnits="userSpaceOnUse">
            <path d="M 20 0 L 0 0 0 20" fill="none" stroke="#f1f5f9" strokeWidth="1"/>
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#grid)" opacity="0.3" />
      </svg>

      {/* CPU Components positioned to match the reference diagram */}
      <div className="relative z-10 w-full h-full">

        {/* Program Counter - Top Left */}
        <div 
          className="absolute"
          style={{ left: '40px', top: '350px', width: '80px', height: '80px' }}
          data-component="pc"
        >
          <ProgramCounter />
        </div>

        {/* PC+4 Adder - Above PC */}
        <div 
          className="absolute"
          style={{ left: '160px', top: '280px', width: '70px', height: '50px' }}
          data-component="pc-adder"
        >
          <div className="cpu-component flex items-center justify-center text-sm font-bold bg-green-100 border-green-300">
            <svg width="40" height="30" viewBox="0 0 40 30">
              <polygon points="5,15 15,5 35,5 35,25 15,25" fill="#dcfce7" stroke="#16a34a" strokeWidth="2"/>
              <text x="20" y="18" textAnchor="middle" fontSize="10" fill="#16a34a">+4</text>
            </svg>
          </div>
        </div>

        {/* Instruction Memory - Center Left */}
        <div 
          className="absolute"
          style={{ left: '160px', top: '350px', width: '140px', height: '180px' }}
          data-component="instruction-memory"
        >
          <InstructionMemory />
        </div>

        {/* Control Unit - Center Top */}
        <div 
          className="absolute"
          style={{ left: '350px', top: '150px', width: '280px', height: '200px' }}
          data-component="control-unit"
        >
          <ControlUnit />
        </div>

        {/* Register File - Center Bottom */}
        <div 
          className="absolute"
          style={{ left: '350px', top: '450px', width: '180px', height: '220px' }}
          data-component="registers"
        >
          <RegisterFile />
        </div>

        {/* Sign Extend - Below Register File */}
        <div 
          className="absolute"
          style={{ left: '350px', top: '700px', width: '100px', height: '60px' }}
          data-component="sign-extend"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-blue-100 border-blue-300">
            <div className="text-center">
              <div>Sign</div>
              <div>Extend</div>
            </div>
          </div>
        </div>

        {/* MUX 1 - Register File Output */}
        <div 
          className="absolute"
          style={{ left: '560px', top: '520px', width: '40px', height: '80px' }}
          data-component="mux-1"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-gray-100">
            <svg width="30" height="60" viewBox="0 0 30 60">
              <polygon points="5,10 25,10 30,30 25,50 5,50" fill="#f3f4f6" stroke="#6b7280" strokeWidth="2"/>
              <text x="15" y="33" textAnchor="middle" fontSize="8">MUX</text>
            </svg>
          </div>
        </div>

        {/* ALU - Center Right */}
        <div 
          className="absolute"
          style={{ left: '700px', top: '420px', width: '120px', height: '160px' }}
          data-component="alu"
        >
          <ALU />
        </div>

        {/* Shift Left 2 - Above ALU */}
        <div 
          className="absolute"
          style={{ left: '650px', top: '350px', width: '80px', height: '40px' }}
          data-component="shift-left"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-yellow-100 border-yellow-300">
            Shift left 2
          </div>
        </div>

        {/* Branch Adder - Top Right */}
        <div 
          className="absolute"
          style={{ left: '850px', top: '280px', width: '70px', height: '50px' }}
          data-component="branch-adder"
        >
          <div className="cpu-component flex items-center justify-center text-sm font-bold bg-green-100 border-green-300">
            <svg width="40" height="30" viewBox="0 0 40 30">
              <polygon points="5,15 15,5 35,5 35,25 15,25" fill="#dcfce7" stroke="#16a34a" strokeWidth="2"/>
              <text x="20" y="18" textAnchor="middle" fontSize="8" fill="#16a34a">ADD</text>
            </svg>
          </div>
        </div>

        {/* MUX 2 - ALU to Data Memory */}
        <div 
          className="absolute"
          style={{ left: '900px', top: '420px', width: '40px', height: '80px' }}
          data-component="mux-2"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-gray-100">
            <svg width="30" height="60" viewBox="0 0 30 60">
              <polygon points="5,10 25,10 30,30 25,50 5,50" fill="#f3f4f6" stroke="#6b7280" strokeWidth="2"/>
              <text x="15" y="33" textAnchor="middle" fontSize="8">MUX</text>
            </svg>
          </div>
        </div>

        {/* Data Memory - Right */}
        <div 
          className="absolute"
          style={{ left: '1000px', top: '380px', width: '160px', height: '200px' }}
          data-component="data-memory"
        >
          <DataMemory />
        </div>

        {/* MUX 3 - PC Source */}
        <div 
          className="absolute"
          style={{ left: '1200px', top: '300px', width: '40px', height: '80px' }}
          data-component="mux-3"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-gray-100">
            <svg width="30" height="60" viewBox="0 0 30 60">
              <polygon points="5,10 25,10 30,30 25,50 5,50" fill="#f3f4f6" stroke="#6b7280" strokeWidth="2"/>
              <text x="15" y="33" textAnchor="middle" fontSize="8">MUX</text>
            </svg>
          </div>
        </div>

        {/* MUX 4 - Write Data to Registers */}
        <div 
          className="absolute"
          style={{ left: '1200px', top: '500px', width: '40px', height: '80px' }}
          data-component="mux-4"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-gray-100">
            <svg width="30" height="60" viewBox="0 0 30 60">
              <polygon points="5,10 25,10 30,30 25,50 5,50" fill="#f3f4f6" stroke="#6b7280" strokeWidth="2"/>
              <text x="15" y="33" textAnchor="middle" fontSize="8">MUX</text>
            </svg>
          </div>
        </div>

        {/* ALU Control - Below ALU */}
        <div 
          className="absolute"
          style={{ left: '720px', top: '620px', width: '80px', height: '60px' }}
          data-component="alu-control"
        >
          <div className="cpu-component flex items-center justify-center text-xs font-bold bg-cyan-100 border-cyan-300">
            <div className="text-center">
              <div>ALU</div>
              <div>Control</div>
            </div>
          </div>
        </div>
      </div>

      {/* Current instruction indicator */}
      {cpu.currentInstruction && (
        <div className="absolute top-4 right-4 bg-cpu-blue text-white px-3 py-2 rounded-lg text-sm font-mono">
          <div className="font-bold">Current Instruction:</div>
          <div>{cpu.currentInstruction.assembly}</div>
          <div className="text-xs opacity-75">
            PC: 0x{cpu.pc.toString(16).toUpperCase().padStart(8, '0')}
          </div>
        </div>
      )}

      {/* Animation mode indicator */}
      <div className="absolute top-4 left-4 flex items-center space-x-2">
        <div className={`w-3 h-3 rounded-full ${mode === 'simulation' ? 'bg-cpu-green animate-pulse' : 'bg-cpu-gray'}`} />
        <span className="text-sm font-medium capitalize">{mode} Mode</span>
      </div>
    </div>
  );
};

export default CPUDatapath;
