import React from 'react';
import CPUDatapath from './components/CPU/CPUDatapath';
import ControlPanel from './components/ControlPanel';
import './index.css';

const App: React.FC = () => {
  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <header className="mb-6">
          <h1 className="text-3xl font-bold text-gray-800 text-center">
            LEGv8 CPU Simulator
          </h1>
          <p className="text-gray-600 text-center mt-2">
            Interactive CPU Architecture Visualization with Animation
          </p>
        </header>

        {/* Main Layout */}
        <div className="grid grid-cols-1 xl:grid-cols-4 gap-6">
          {/* CPU Datapath Visualization */}
          <div className="xl:col-span-3">
            <div className="bg-white rounded-lg shadow-lg p-4 h-[600px]">
              <h2 className="text-lg font-semibold text-gray-800 mb-4">
                CPU Datapath
              </h2>
              <div className="h-full">
                <CPUDatapath />
              </div>
            </div>
          </div>

          {/* Control Panel */}
          <div className="xl:col-span-1">
            <ControlPanel />
          </div>
        </div>

        {/* Footer */}
        <footer className="mt-8 text-center text-gray-500 text-sm">
          <p>
            LEGv8 Simulator - Built with React, TypeScript, and GSAP
          </p>
          <p className="mt-1">
            Supports simulation and realtime execution modes
          </p>
        </footer>
      </div>
    </div>
  );
};

export default App;
